class Color:
    def __init__(self, color):
        self.color = color

    def _repr_markdown_(self):
        style = (
            f"background-color: {self.color}; width: 50px; height: 50px; margin: 10px"
        )
        return f'&lt;div style="{style}"&gt;&lt;/div&gt;'


Color("green")
